mapper.insert:

         SELECT pack_heart_seq.nextval 
         FROM DUAL

	      INSERT INTO 
	      pack_heart (
	         pack_heart_idx
	         , userinfo_id
	         , pack_idx
	      ) 
	      VALUES (
	        #{packHeartIdx}
	        , #{userinfoId}
	        , #{packIdx}
	      )

mapper.delete:
DELETE FROM pack_heart 
WHERE pack_heart_idx=#{packHeartIdx}
AND userinfo_id=#{userinfoId}
