mapper.insert:

         SELECT pack_seq.nextval 
         FROM DUAL

	      INSERT INTO 
	      pack (
	         pack_idx
	         , pack_kind
	         , pack_title
	         , pack_startdate
	         , pack_enddate
	         , pack_regdate
	         , pack_people
	         , pack_childprice
	         , pack_adultprice
	         , pack_previewimg
	         , pack_slideimg1
	         , pack_slideimg2
	         , pack_slideimg3
	         , pack_contentimg1
	         , pack_contentimg2
	         , pack_contentimg3
	         , pack_calendarimg
	         , pack_status
	      ) 
	      VALUES (
	         #{packIdx}
	         , #{packKind}
	         , #{packTitle}
	         , TO_DATE(#{packStartDate}, 'YYYY-MM-DD')
	         , TO_DATE(#{packEndDate}, 'YYYY-MM-DD')   
	         , sysdate
	         , #{packPeople}
	         , #{packChildPrice}
	         , #{packAdultPrice}
	         , #{packPreviewImg}
	         , #{packSlideImg1}
	         , #{packSlideImg2}
	         , #{packSlideImg3}
	         , #{packContentImg1}
	         , #{packContentImg2}
	         , #{packContentImg3}
	         , #{packCalendarImg}
	         , #{packStatus}
	      )
